mapper.update:
UPDATE BOARD
TITLE = #{title}, CONTENT = #{content}, THUMBNAIL_NAME = #{thumbnailName}, THUMBNAIL_PATH = #{thumbnailPath}, UPDATED_AT = NOW()
WHERE ID = #{id}

mapper.select:
SELECT * FROM BOARD_VIEW
WHERE MEMBER_ID = #{memberId}

    AND CATEGORY_ID = #{cri.category}

LIMIT #{cri.size} OFFSET #{cri.offset}

SELECT COUNT(ID)
FROM BOARD
WHERE MEMBER_ID = #{memberId}

  AND CATEGORY_ID = #{categoryId}
